(()=>{"use strict";(e=>{const t=document.querySelector("#timer-days"),n=document.querySelector("#timer-hours"),o=document.querySelector("#timer-minutes"),s=document.querySelector("#timer-seconds"),r=()=>{let e=(new Date("25 march 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,days:Math.floor(e/60/60/24),hours:Math.floor(e/60/60)%24,minutes:Math.floor(e/60)%60,seconds:Math.floor(e%60)}};let u=r(),m=setInterval((()=>{u.timeRemaining>0?(()=>{let e=r();5>e.days%10>1?t.textContent=e.days+" дня /":e.days%10==1&&11!=e.days?t.textContent=e.days+" день /":t.textContent=e.days+" дней /",e.hours<10&&e.hours>=0?n.textContent="0"+e.hours+" ч":n.textContent=e.hours+" ч",e.minutes<10&&e.minutes>=0?o.textContent="0"+e.minutes+" м":o.textContent=e.minutes+" м",e.seconds<10&&e.seconds>=0?s.textContent="0"+e.seconds+" с":s.textContent=e.seconds+" с"})():(clearTimeout(m),console.log("Остановка!"))}),1e3)})()})();